import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const PokedexContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
`;

const SearchBar = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const PokemonCard = styled.div`
  background: #f5f5f5;
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
`;

const Pokedex = () => {
  const [pokemon, setPokemon] = useState(null);
  const [search, setSearch] = useState('');
  const [error, setError] = useState('');

  const searchPokemon = async () => {
    try {
      setError('');
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${search.toLowerCase()}`);
      const evolutionChain = await axios.get(response.data.species.url);
      const pokemon = {
        id: response.data.id,
        name: response.data.name,
        types: response.data.types.map(type => type.type.name),
        stats: response.data.stats.map(stat => ({
          name: stat.stat.name,
          value: stat.base_stat
        })),
        moves: response.data.moves.map(move => move.move.name),
        image: response.data.sprites.other['official-artwork'].front_default,
        height: response.data.height / 10, // Convert to meters
        weight: response.data.weight / 10, // Convert to kilograms
      };
      setPokemon(pokemon);
    } catch (err) {
      setError('Pokemon not found');
      setPokemon(null);
    }
  };

  return (
    <PokedexContainer>
      <h1>Pok√©dex</h1>
      <SearchBar>
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Enter Pokemon name or number"
        />
        <button onClick={searchPokemon}>Search</button>
      </SearchBar>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {pokemon && (
        <PokemonCard>
          <img src={pokemon.image} alt={pokemon.name} style={{ width: '200px' }} />
          <h2>#{pokemon.id} {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}</h2>
          
          <h3>Types:</h3>
          <p>{pokemon.types.join(', ')}</p>

          <h3>Stats:</h3>
          {pokemon.stats.map(stat => (
            <div key={stat.name}>
              <p>{stat.name}: {stat.value}</p>
            </div>
          ))}

          <h3>Dimensions:</h3>
          <p>Height: {pokemon.height}m</p>
          <p>Weight: {pokemon.weight}kg</p>

          <h3>Moves:</h3>
          <p>{pokemon.moves.slice(0, 5).join(', ')}...</p>
        </PokemonCard>
      )}
    </PokedexContainer>
  );
};

export default Pokedex;
